import React, { useMemo } from 'react';
import { LineChart, Line, ResponsiveContainer, Tooltip, XAxis, YAxis } from 'recharts';
import './PriceChart.css';

const PriceChart = ({ priceHistory, currentPrice }) => {
  // Memoize the processed data to prevent unnecessary re-renders
  const chartData = useMemo(() => {
    if (!priceHistory || priceHistory.length === 0) {
      return [];
    }
    
    // Ensure we have at least 2 data points for trend calculation
    return priceHistory.map((point, index) => ({
      ...point,
      index,
      // Add a unique key for smooth animations
      key: `${point.time}-${point.price}-${index}`
    }));
  }, [priceHistory]);

  // Determine if price is trending up or down with more data points
  const isTrendingUp = useMemo(() => {
    if (chartData.length < 2) return true;
    
    // Use last 3 points for more stable trend detection
    const recentPoints = chartData.slice(-3);
    if (recentPoints.length < 2) return true;
    
    const firstPrice = recentPoints[0].price;
    const lastPrice = recentPoints[recentPoints.length - 1].price;
    return lastPrice >= firstPrice;
  }, [chartData]);

  // Calculate price change for display
  const priceChange = useMemo(() => {
    if (chartData.length < 2) return 0;
    const lastPrice = chartData[chartData.length - 1].price;
    const previousPrice = chartData[chartData.length - 2].price;
    return lastPrice - previousPrice;
  }, [chartData]);

  // Don't render if no data
  if (chartData.length === 0) {
    return (
      <div className="price-chart">
        <div className="chart-header">
          <span className="chart-label">Price Trend (No data)</span>
          <span className="trend-indicator neutral">→</span>
        </div>
        <div className="chart-placeholder">
          <span>Waiting for price data...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="price-chart">
      <div className="chart-header">
        <span className="chart-label">Price Trend ({chartData.length} points)</span>
        <span className={`trend-indicator ${isTrendingUp ? 'up' : 'down'}`}>
          {isTrendingUp ? '↗' : '↘'}
        </span>
      </div>
      
      <ResponsiveContainer width="100%" height={80}>
        <LineChart 
          data={chartData} 
          margin={{ top: 5, right: 5, left: 5, bottom: 5 }}
          key={`chart-${chartData.length}`} // Force re-render when data changes
        >
          <defs>
            <linearGradient id={`priceGradient-${isTrendingUp ? 'up' : 'down'}`} x1="0" y1="0" x2="0" y2="1">
              <stop offset="5%" stopColor={isTrendingUp ? "#00ff88" : "#ff4444"} stopOpacity={0.8}/>
              <stop offset="95%" stopColor={isTrendingUp ? "#00ff88" : "#ff4444"} stopOpacity={0.1}/>
            </linearGradient>
          </defs>
          
          {/* Hidden axes for better scaling */}
          <XAxis 
            dataKey="index" 
            hide={true}
            domain={['dataMin', 'dataMax']}
          />
          <YAxis 
            hide={true}
            domain={['dataMin - 10', 'dataMax + 10']}
          />
          
          <Line
            type="monotone"
            dataKey="price"
            stroke={isTrendingUp ? "#00ff88" : "#ff4444"}
            strokeWidth={2}
            dot={false}
            activeDot={{ 
              r: 4, 
              fill: isTrendingUp ? "#00ff88" : "#ff4444",
              stroke: "#ffffff",
              strokeWidth: 2
            }}
            fill={`url(#priceGradient-${isTrendingUp ? 'up' : 'down'})`}
            animationDuration={300}
            animationBegin={0}
            isAnimationActive={true}
            connectNulls={true}
          />
          
          <Tooltip
            contentStyle={{
              backgroundColor: '#1e1e2e',
              border: '1px solid #3a3a4e',
              borderRadius: '8px',
              color: '#ffffff',
              boxShadow: '0 4px 12px rgba(0, 0, 0, 0.3)'
            }}
            labelStyle={{ color: '#b0b0b0' }}
            formatter={(value) => [`₹${value.toLocaleString()}`, 'Price']}
            labelFormatter={(label) => {
              const dataPoint = chartData[label];
              return dataPoint ? `Time: ${dataPoint.time}` : 'Unknown';
            }}
            cursor={{
              stroke: isTrendingUp ? "#00ff88" : "#ff4444",
              strokeWidth: 1,
              strokeDasharray: "3 3"
            }}
          />
        </LineChart>
      </ResponsiveContainer>
      
      <div className="chart-footer">
        <span className="current-price">₹{currentPrice?.toLocaleString() || '0'}</span>
        <span className={`price-change ${priceChange >= 0 ? 'positive' : 'negative'}`}>
          {priceChange >= 0 ? '+' : ''}₹{Math.abs(priceChange).toLocaleString()}
        </span>
      </div>
    </div>
  );
};

export default PriceChart;
