    { time: '19:11', commodity: 'FREAC', action: 'SELL', quantity: 11, value: 56903 },
    { time: '19:10', commodity: 'ACORE', action: 'BUY', quantity: 47, value: 61147 },
    { time: '19:09', commodity: 'QCRYS', action: 'SELL', quantity: 73, value: 2190 },
    { time: '19:08', commodity: 'DMATT', action: 'BUY', quantity: 25, value: 23225 },
    { time: '19:07', commodity: 'NPROC', action: 'SELL', quantity: 15, value: 4560 }
  ]);

  const [notifications, setNotifications] = useState([]);
  
  // AI Trading System
  const [aiTraders, setAiTraders] = useState([]);
  const [aiTradeFeed, setAiTradeFeed] = useState([]);
  const [showAITraders, setShowAITraders] = useState(false);
  const [showMarketSentiment, setShowMarketSentiment] = useState(false);

  const handleQuantityChange = (commodityId, newQuantity) => {
    setCommodities(prev => prev.map(commodity => 
      commodity.id === commodityId 
        ? { ...commodity, quantity: Math.max(1, Math.min(100, newQuantity)) }
        : commodity
    ));
  };

  const handleBuy = (commodityId) => {
    const commodity = commodities.find(c => c.id === commodityId);
    const cost = commodity.buyPrice * commodity.quantity;
    
    if (gameState.credits >= cost) {
      setGameState(prev => ({
        ...prev,
        credits: prev.credits - cost,
        totalMargin: prev.totalMargin + cost
      }));
      
      // Update holdings
      setCommodities(prev => prev.map(c => 
        c.id === commodityId 
          ? { ...c, holdings: c.holdings + c.quantity }
          : c
      ));
      
      // Add to trade feed
      const newTrade = {
        time: new Date().toLocaleTimeString('en-US', { hour12: false, hour: '2-digit', minute: '2-digit' }),
        commodity: commodity.name.substring(0, 5).toUpperCase(),
        action: 'BUY',
        quantity: commodity.quantity,
        value: cost
      };
      
      setTradeFeed(prev => [newTrade, ...prev.slice(0, 4)]);
      
      // Show success notification
      showNotification({
        type: 'buy',
        title: 'Trade Successful!',
        message: `Bought ${commodity.quantity} ${commodity.name} for ₹${cost.toLocaleString()}`
      });
    } else {
      // Show error notification
      showNotification({
        type: 'error',
        title: 'Insufficient Credits',
        message: `You need ₹${cost.toLocaleString()} but only have ₹${gameState.credits.toLocaleString()}`
      });
    }
  };

  const handleSell = (commodityId, sellQuantity = null) => {
    const commodity = commodities.find(c => c.id === commodityId);
    const quantityToSell = sellQuantity !== null ? sellQuantity : commodity.quantity;
    const revenue = commodity.sellPrice * quantityToSell;
    
    // Check if user has enough holdings to sell
    if (commodity.holdings >= quantityToSell) {
      setGameState(prev => ({
        ...prev,
        credits: prev.credits + revenue,
        profit: prev.profit + (revenue - commodity.buyPrice * quantityToSell)
      }));
      
      // Update holdings
      setCommodities(prev => prev.map(c => 
        c.id === commodityId 
          ? { ...c, holdings: c.holdings - quantityToSell }
          : c
      ));
      
      // Add to trade feed
      const newTrade = {
        time: new Date().toLocaleTimeString('en-US', { hour12: false, hour: '2-digit', minute: '2-digit' }),
        commodity: commodity.name.substring(0, 5).toUpperCase(),
        action: 'SELL',
        quantity: quantityToSell,
        value: revenue
      };
      
      setTradeFeed(prev => [newTrade, ...prev.slice(0, 4)]);
      
      // Show success notification
      showNotification({
        type: 'sell',
        title: 'Trade Successful!',
        message: `Sold ${quantityToSell} ${commodity.name} for ₹${revenue.toLocaleString()}`
      });
    } else {
      // Show error notification for insufficient holdings
      showNotification({
        type: 'error',
        title: 'Insufficient Holdings',
        message: `You only have ${commodity.holdings} ${commodity.name} but trying to sell ${quantityToSell}`
      });
    }
  };

  const toggleSimulation = () => {
    setGameState(prev => ({
      ...prev,
      isSimulationPaused: !prev.isSimulationPaused
    }));
  };

  const showNotification = (notificationData) => {
    const newNotification = {
      id: Date.now() + Math.random(),
      ...notificationData
    };
    
    setNotifications(prev => [...prev, newNotification]);
  };

  const removeNotification = (notificationId) => {
    setNotifications(prev => prev.filter(n => n.id !== notificationId));
  };

  // Initialize AI traders
  useEffect(() => {
    if (aiTraders.length === 0) {
      setAiTraders(generateAITraders());
    }
  }, [aiTraders.length]);

  // AI Trading simulation
  useEffect(() => {
    if (!gameState.isSimulationPaused && aiTraders.length > 0) {
      const interval = setInterval(() => {
        // Generate trading decisions for all AI traders
        const allDecisions = [];
        
        aiTraders.forEach(trader => {
          const decisions = generateTradingDecision(trader, commodities, { commodities, marketTrends }, aiTradeFeed);
          allDecisions.push(...decisions);
        });
        
        // Execute trades
        if (allDecisions.length > 0) {
          const executedTrades = [];
          const updatedTraders = [...aiTraders];
          
          allDecisions.forEach(decision => {
            const traderIndex = updatedTraders.findIndex(t => t.id === decision.traderId);
            if (traderIndex !== -1) {
              // Execute the trade
              updatedTraders[traderIndex] = executeTrade(updatedTraders[traderIndex], decision, commodities);
              
              // Add to AI trade feed
              const aiTrade = {
                time: new Date().toLocaleTimeString('en-US', { hour12: false, hour: '2-digit', minute: '2-digit' }),
                trader: decision.traderName,
                commodity: decision.commodityName.substring(0, 5).toUpperCase(),
                action: decision.action,
                quantity: decision.quantity,
                value: decision.quantity * decision.price,
                strategy: decision.strategy,
                emotionalState: decision.emotionalState
              };
              
              executedTrades.push(aiTrade);
            }
          });
          
          // Update AI traders
          setAiTraders(updatedTraders.map(trader => updateTraderPerformance(trader)));
          
          // Update AI trade feed
          setAiTradeFeed(prev => [...executedTrades, ...prev.slice(0, 19)]); // Keep last 20 trades
          
          // Update main trade feed with some AI trades
          if (executedTrades.length > 0) {
            const selectedTrades = executedTrades.slice(0, 3); // Show up to 3 AI trades
            const newTradeFeed = selectedTrades.map(trade => ({
              time: trade.time,
              commodity: trade.commodity,
              action: trade.action,
              quantity: trade.quantity,
              value: trade.value
            }));
            
            setTradeFeed(prev => [...newTradeFeed, ...prev.slice(0, 2)]); // Keep last 5 trades
          }
        }
      }, 5000); // AI traders make decisions every 5 seconds

      return () => clearInterval(interval);
    }
  }, [gameState.isSimulationPaused, aiTraders, commodities, marketTrends, aiTradeFeed]);

  const handleTraderSelect = (trader) => {
    console.log('Selected trader:', trader);
  };

  // Simulate price fluctuations based on supply and demand
  useEffect(() => {
    if (!gameState.isSimulationPaused) {
      const interval = setInterval(() => {
        setCommodities(prev => prev.map(commodity => {
          // Calculate supply-demand ratio (higher ratio = lower price)
          const supplyDemandRatio = commodity.supply / commodity.demand;
          
          // Calculate price change based on supply-demand relationship
          // As supply increases relative to demand, price decreases
          // As demand increases relative to supply, price increases
          const basePriceChange = (1 - supplyDemandRatio) * 0.05; // Base change based on ratio
          
          // Add some randomness to make it more realistic
          const randomFactor = (Math.random() - 0.5) * 0.02; // ±1% random variation
          const totalPriceChange = basePriceChange + randomFactor;
          
          // Calculate new price
          const newLastPrice = Math.max(1, Math.round(commodity.lastPrice * (1 + totalPriceChange)));
          
          // Update buy/sell prices with spread
          const spread = Math.floor(Math.random() * 3) + 1;
          const newBuyPrice = Math.max(1, newLastPrice - spread);
          const newSellPrice = newLastPrice + spread;
          
          // Update price history
          const currentTime = new Date().toLocaleTimeString('en-US', { hour12: false, hour: '2-digit', minute: '2-digit' });
          const newPriceHistory = [...commodity.priceHistory.slice(1), { time: currentTime, price: newLastPrice }];
          
          // Simulate supply and demand changes
          const supplyChange = Math.floor((Math.random() - 0.5) * 100); // ±50 supply change
          const demandChange = Math.floor((Math.random() - 0.5) * 50); // ±25 demand change
          
          const newSupply = Math.max(100, commodity.supply + supplyChange);
          const newDemand = Math.max(100, commodity.demand + demandChange);
          
          return {
            ...commodity,
            lastPrice: newLastPrice,
            buyPrice: newBuyPrice,
            sellPrice: newSellPrice,
            supply: newSupply,
            demand: newDemand,
            priceHistory: newPriceHistory
          };
        }));
      }, 3000);

      return () => clearInterval(interval);
    }
  }, [gameState.isSimulationPaused]);

  return (
    <div className="App">
      <Header 
        gameState={gameState} 
        onToggleSimulation={toggleSimulation}
      />
      <div className="main-content">
        <TradingGrid 
          commodities={commodities}
          onQuantityChange={handleQuantityChange}
          onBuy={handleBuy}
          onSell={handleSell}
        />
        <div className="sidebar-container">
          <Sidebar 
            aiPortfolio={aiPortfolio}
            marketTrends={marketTrends}
            tradeFeed={tradeFeed}
            aiTradeFeed={aiTradeFeed}
          />
          
          {/* AI Trading Controls */}
          <div className="ai-controls">
            <button 
              className={`ai-control-btn ${showAITraders ? 'active' : ''}`}
              onClick={() => setShowAITraders(!showAITraders)}
            >
              🤖 AI Traders ({aiTraders.length})
            </button>
            <button 
              className={`ai-control-btn ${showMarketSentiment ? 'active' : ''}`}
              onClick={() => setShowMarketSentiment(!showMarketSentiment)}
            >
              📊 Market Sentiment
            </button>
          </div>
          
          {/* AI Trader Panel */}
          {showAITraders && (
            <div className="ai-panel-overlay">
              <AITraderPanel 
                aiTraders={aiTraders}
                onTraderSelect={handleTraderSelect}
              />
            </div>
          )}
          
          {/* Market Sentiment Panel */}
          {showMarketSentiment && (
            <div className="ai-panel-overlay">
              <MarketSentiment 
                aiTraders={aiTraders}
                commodities={commodities}
              />
            </div>
          )}
        </div>
      </div>
      <NotificationContainer 
        notifications={notifications}
        onRemoveNotification={removeNotification}
      />
    </div>
  );
}

export default App;
