      quantity: 1,
      holdings: 0,
      priceHistory: [
        { time: '00:00', price: 850 }, { time: '04:00', price: 880 }, { time: '08:00', price: 920 },
        { time: '12:00', price: 950 }, { time: '16:00', price: 940 }, { time: '20:00', price: 929 }
      ]
    },
    {
      id: 'neural-processors',
      name: 'Neural Processors',
      icon: 'ðŸ§ ',
      lastPrice: 304,
      supply: 8886,
      demand: 2359,
      buyPrice: 298,
      sellPrice: 310,
      buyQuantity: 1500,
      sellQuantity: 1000,
      unit: 'Units',
      quantity: 1,
      holdings: 0,
      priceHistory: [
        { time: '00:00', price: 280 }, { time: '04:00', price: 290 }, { time: '08:00', price: 300 },
        { time: '12:00', price: 310 }, { time: '16:00', price: 305 }, { time: '20:00', price: 304 }
      ]
    },
    {
      id: 'antimatter-cores',
      name: 'Antimatter Cores',
      icon: 'âš›ï¸',
      lastPrice: 1347,
      supply: 623,
      demand: 1911,
      buyPrice: 1320,
      sellPrice: 1374,
      buyQuantity: 500,
      sellQuantity: 800,
      unit: 'Units',
      quantity: 1,
      holdings: 0,
      priceHistory: [
        { time: '00:00', price: 1300 }, { time: '04:00', price: 1320 }, { time: '08:00', price: 1350 },
        { time: '12:00', price: 1380 }, { time: '16:00', price: 1360 }, { time: '20:00', price: 1347 }
      ]
    },
    {
      id: 'plasma-cells',
      name: 'Plasma Cells',
      icon: 'âš¡',
      lastPrice: 77,
      supply: 4618,
      demand: 3229,
      buyPrice: 75,
      sellPrice: 79,
      buyQuantity: 2000,
      sellQuantity: 1500,
      unit: 'Units',
      quantity: 1,
      holdings: 0,
      priceHistory: [
        { time: '00:00', price: 70 }, { time: '04:00', price: 72 }, { time: '08:00', price: 75 },
        { time: '12:00', price: 78 }, { time: '16:00', price: 76 }, { time: '20:00', price: 77 }
      ]
    },
    {
      id: 'fusion-reactors',
      name: 'Fusion Reactors',
      icon: 'ðŸ”‹',
      lastPrice: 5235,
      supply: 214,
      demand: 800,
      buyPrice: 5130,
      sellPrice: 5340,
      buyQuantity: 100,
      sellQuantity: 300,
      unit: 'Units',
      quantity: 1,
      holdings: 0,
      priceHistory: [
        { time: '00:00', price: 5000 }, { time: '04:00', price: 5100 }, { time: '08:00', price: 5200 },
        { time: '12:00', price: 5300 }, { time: '16:00', price: 5250 }, { time: '20:00', price: 5235 }
      ]
    }
  ]);

  const [aiPortfolio, setAiPortfolio] = useState({
    'Quantum Crystals': 12345,
    'Dark Matter': 8901,
    'Neural Processors': 15678,
    'Plasma Cells': 4567
  });

  const [marketTrends, setMarketTrends] = useState([
    { name: 'Quantum Crystals', trend: 'Bullish', color: '#00ff88' },
    { name: 'Dark Matter', trend: 'Bearish', color: '#ff4444' },
    { name: 'Neural Processors', trend: 'Bullish', color: '#00ff88' }
  ]);

  const [tradeFeed, setTradeFeed] = useState([
    { time: '19:11', commodity: 'FREAC', action: 'SELL', quantity: 11, value: 56903 },
    { time: '19:10', commodity: 'ACORE', action: 'BUY', quantity: 47, value: 61147 },
    { time: '19:09', commodity: 'QCRYS', action: 'SELL', quantity: 73, value: 2190 },
    { time: '19:08', commodity: 'DMATT', action: 'BUY', quantity: 25, value: 23225 },
    { time: '19:07', commodity: 'NPROC', action: 'SELL', quantity: 15, value: 4560 }
  ]);

  const [notifications, setNotifications] = useState([]);

  const handleQuantityChange = (commodityId, newQuantity) => {
    setCommodities(prev => prev.map(commodity => 
      commodity.id === commodityId 
        ? { ...commodity, quantity: Math.max(1, Math.min(100, newQuantity)) }
        : commodity
    ));
  };

  const handleBuy = (commodityId) => {
    const commodity = commodities.find(c => c.id === commodityId);
    const cost = commodity.buyPrice * commodity.quantity;
    
    if (gameState.credits >= cost) {
      setGameState(prev => ({
        ...prev,
        credits: prev.credits - cost,
        totalMargin: prev.totalMargin + cost
      }));
      
      // Update holdings
      setCommodities(prev => prev.map(c => 
        c.id === commodityId 
          ? { ...c, holdings: c.holdings + c.quantity }
          : c
      ));
      
      // Add to trade feed
      const newTrade = {
        time: new Date().toLocaleTimeString('en-US', { hour12: false, hour: '2-digit', minute: '2-digit' }),
        commodity: commodity.name.substring(0, 5).toUpperCase(),
        action: 'BUY',
        quantity: commodity.quantity,
        value: cost
      };
      
      setTradeFeed(prev => [newTrade, ...prev.slice(0, 4)]);
      
      // Show success notification
      showNotification({
        type: 'buy',
        title: 'Trade Successful!',
        message: `Bought ${commodity.quantity} ${commodity.name} for â‚¹${cost.toLocaleString()}`
      });
    } else {
      // Show error notification
      showNotification({
        type: 'error',
        title: 'Insufficient Credits',
        message: `You need â‚¹${cost.toLocaleString()} but only have â‚¹${gameState.credits.toLocaleString()}`
      });
    }
  };

  const handleSell = (commodityId, sellQuantity = null) => {
    const commodity = commodities.find(c => c.id === commodityId);
    const quantityToSell = sellQuantity !== null ? sellQuantity : commodity.quantity;
    const revenue = commodity.sellPrice * quantityToSell;
    
    // Check if user has enough holdings to sell
    if (commodity.holdings >= quantityToSell) {
      setGameState(prev => ({
        ...prev,
        credits: prev.credits + revenue,
        profit: prev.profit + (revenue - commodity.buyPrice * quantityToSell)
      }));
      
      // Update holdings
      setCommodities(prev => prev.map(c => 
        c.id === commodityId 
          ? { ...c, holdings: c.holdings - quantityToSell }
          : c
      ));
      
      // Add to trade feed
      const newTrade = {
        time: new Date().toLocaleTimeString('en-US', { hour12: false, hour: '2-digit', minute: '2-digit' }),
        commodity: commodity.name.substring(0, 5).toUpperCase(),
        action: 'SELL',
        quantity: quantityToSell,
        value: revenue
      };
      
      setTradeFeed(prev => [newTrade, ...prev.slice(0, 4)]);
      
      // Show success notification
      showNotification({
        type: 'sell',
        title: 'Trade Successful!',
        message: `Sold ${quantityToSell} ${commodity.name} for â‚¹${revenue.toLocaleString()}`
      });
    } else {
      // Show error notification for insufficient holdings
      showNotification({
        type: 'error',
        title: 'Insufficient Holdings',
        message: `You only have ${commodity.holdings} ${commodity.name} but trying to sell ${quantityToSell}`
      });
    }
  };

  const toggleSimulation = () => {
    setGameState(prev => ({
      ...prev,
      isSimulationPaused: !prev.isSimulationPaused
    }));
  };

  const showNotification = (notificationData) => {
    const newNotification = {
      id: Date.now() + Math.random(),
      ...notificationData
    };
    
    setNotifications(prev => [...prev, newNotification]);
  };

  const removeNotification = (notificationId) => {
    setNotifications(prev => prev.filter(n => n.id !== notificationId));
  };

  // Simulate price fluctuations
  useEffect(() => {
    if (!gameState.isSimulationPaused) {
      const interval = setInterval(() => {
        setCommodities(prev => prev.map(commodity => {
          const priceChange = (Math.random() - 0.5) * 0.1; // Â±5% change
          const newLastPrice = Math.max(1, Math.round(commodity.lastPrice * (1 + priceChange)));
          const newBuyPrice = Math.max(1, newLastPrice - Math.floor(Math.random() * 3) - 1);
          const newSellPrice = newLastPrice + Math.floor(Math.random() * 3) + 1;
          
          // Update price history
          const currentTime = new Date().toLocaleTimeString('en-US', { hour12: false, hour: '2-digit', minute: '2-digit' });
          const newPriceHistory = [...commodity.priceHistory.slice(1), { time: currentTime, price: newLastPrice }];
          
          return {
            ...commodity,
            lastPrice: newLastPrice,
            buyPrice: newBuyPrice,
            sellPrice: newSellPrice,
            priceHistory: newPriceHistory
          };
        }));
      }, 3000);

      return () => clearInterval(interval);
    }
  }, [gameState.isSimulationPaused]);

  return (
    <div className="App">
      <Header 
        gameState={gameState} 
        onToggleSimulation={toggleSimulation}
      />
      <div className="main-content">
        <TradingGrid 
          commodities={commodities}
          onQuantityChange={handleQuantityChange}
          onBuy={handleBuy}
          onSell={handleSell}
        />
        <Sidebar 
          aiPortfolio={aiPortfolio}
          marketTrends={marketTrends}
          tradeFeed={tradeFeed}
        />
      </div>
      <NotificationContainer 
        notifications={notifications}
        onRemoveNotification={removeNotification}
      />
    </div>
  );
}

export default App;
